}

.news-item a {
    color: #60A5FA; /* Blue-400 */
    text-decoration: none;
    transition: color 0.3s ease;
}

.news-item a:hover {
    color: #3B82F6; /* Blue-500 */
}

/* Footer */
footer {
    background-color: #1F2937; /* Gray-800 */
    padding: 2rem 0;
    color: #D1D5DB; /* Gray-300 */
