/* style.css */

body {
    font-family: Arial, sans-serif;
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    background-color: #f0f0f0; /* Placeholder: Background color */
    margin: 0;
}

.game-container {
    text-align: center;
}

.game-board {
    display: grid;
    grid-template-columns: repeat(3, 100px); /* Placeholder: Cell size */
    grid-gap: 5px; /* Placeholder: Gap between cells */
    background-color: #333; /* Placeholder: Board background color */
    border: 2px solid #333;
    padding: 5px;
}

.cell {
    width: 100px; /* Placeholder: Cell size */
    height: 100px; /* Placeholder: Cell size */
    background-color: #fff; /* Placeholder: Cell background color */
    font-size: 60px; /* Placeholder: Text size */
    font-weight: bold;
    color: #333; /* Placeholder: Text color */
    display: flex;
    justify-content: center;
    align-items: center;
    cursor: pointer;
    border: none;
}

.cell:hover {
    background-color: #eee; /* Placeholder: Cell hover color */
}

/* Placeholder: Add more styles as needed */

